import React, { useState } from "react";
import logo from "../Image/logo.png";


export default function Signup1() {

  const [user, setUser] = useState({
    name : "",
    email : "",
    password: ""
  })

  onInputeChange = (e) =>{
    setUser({ ...user, [e.target.name]: e.target.value })
  }

  console.log(user)

      function showPassword() {

             const x = document.getElementById("myInput");
             console.log(x)
            // if (x.type === "password") {
            //   x.type = "text";
            // } else {
            //   x.type ="password";
            // }
          }


  return (
    <>
      <section className="flex-container">
        <div className="info">
          <figure>
            <img src={logo} />
          </figure>
          <h2>
            Provide your real name for identity. Your name will be used for
            search
          </h2>
        </div>
        <div className="about">
          <h1>Let Us Know About You</h1>
          <form>
            <label htmlFor="fname">Username</label>
            <input type="text" name="name" placeholder="Sandy"
            value={user.name}
            onChange={(e) => onInputeChange(e)}
            required
            />
            <label htmlFor="lname">Email</label>
            <input
              type="email"
              name="email"
              placeholder="sandy@gmail.com"
              value={user.email}
              onChange={(e) => onInputeChange(e)}
              required
            />
            <label htmlFor="lname">Password</label>
            <input
              type="password"
              name="password"
              id="myInput"
              placeholder="*******"

              value={user.password}
              onChange={(e) => onInputeChange(e)}
              required

            />
            <span
               onClick={showPassword}
              className="fa fa-fw fa-eye field-icon toggle-password"
            />
            <input className="Submit" type="submit" value="Next" />
          </form>
        </div>
      </section>
    </>
  );
}
