import React, { useState } from 'react'
import logo from "../Image/logo.png";
import { useHistory } from 'react-router-dom';

import Male from "../Image/Male.png"
import Female from "../Image/Female.png"




export default function Signup5() {


      const history = useHistory();
      const [user, setuser] = useState({
            weight: "",
      });


      const onSubmitHandelar = (e) => {
        e.preventDefault();
        console.log("You have submitted:", user);

        // localStorage.setItem("signup2",JSON.stringify(user))
        // history.push("/signup-3")
      };

      const handleOptionChange = (e) => {
        setuser({ ...user, weight: e.target.value });
      };
      return (
            <div>
                   <>
      <section className="flex-container">
        <div className="info">
          <figure>
            <img src={logo} />
          </figure>
          <h2>
            Definig your gender will let us know bring new features for mothers health care.
          </h2>
        </div>
        <div className="about">
          <h1>What is Your Gender</h1>
          <form  onSubmit={onSubmitHandelar}>
             <div className="circle-container">
             <label className="Signup4container">
              <input
                className="Signup4Input"
                type="radio"
                name="radio"

                 value="Less Then 50 Kg"
                 checked={user.weight === "Less Then 50 Kg"}
                 onChange={handleOptionChange}

              />
              <img className="Signup4Image" src={Male} />
            </label>
            <label className="Signup4container">
              <input
              className="Signup4Input"
                type="radio"
                name="radio"  

                value="Gretter then 50 Kg"
                checked={user.weight === "Gretter then 50 Kg"}
                onChange={handleOptionChange}
              />
             <img src={Female} />
            </label>
             </div>
            <input className="Submit4" type="submit" value="Next" />
          </form>
        </div>
      </section>
    </>
            </div>
      )
}
