import React, { useState } from "react";
import logo from "../Image/logo.png";
import { useHistory } from "react-router-dom";

import Upload from "../Image/upload.png";

export default function Signup8() {
  const [selectedFile, setSelectedFile] = useState();
  const [isFilePicked, setIsFilePicked] = useState(false);
  // const[finalinfo,setfinalinfo]=useState({
  //   name : localStorage.getItem(signup1.name),
  //   email : localStorage.getItem("signup1"),
  //   password: localStorage.getItem("signup1")
  // })

  const user = JSON.parse(localStorage.getItem('signup1'));

console.log(user.name)


  const changeHandler = (e) => {
    const reader = new FileReader();
    reader.onload = () => {
      if (reader.readyState === 2) {
        setSelectedFile(reader.result);
        setIsFilePicked(true);
      }
    };
    reader.readAsDataURL(e.target.files[0]);
  };
  const handleSubmission = (e) => {
    e.preventDefault();
    localStorage.setItem("Signup6", JSON.stringify(selectedFile.name));


    // history.push("/signup-1");
  };

  return (
    <>
      <section className="flex-container">
        <div className="info">
          <figure>
            <img src={logo} />
          </figure>
          <h2>
            Please Do not use a model or doll avatar.Please keep faith on
            yourself,you're beautiful too
          </h2>
        </div>
        <div className="about">
          <h1>Yor're almost done</h1>
          <form onSubmit={handleSubmission}>
            <div className="circle-container">
              <label className="Signup8container">
                <input
                  className="Signup8Input"
                  type="file"
                  accept="image/*"
                  onChange={(e) => changeHandler(e)}
                />
                {isFilePicked ? (
                  <div>
                    <img className="imageView" src={selectedFile}></img>
                  </div>
                ) : (
                  <img className="Signup8Image" src={Upload} />
                )}
              </label>
            </div>
            <input className="Submit8" type="submit" value="Start Journy" />
          </form>
        </div>
      </section>
    </>
  );
}
